        stars.push({StarFull})
    }
    // replace an emptyStar by a filled star for every rating point
    for (let i = 0; i < props.rating; i++) {
        stars.splice(i, 1, {StarEmpty})
    }
    return (
        <div>
            {stars}
        </div>
    )
}
