import React from "react";
import {Link} from "react-router-dom";
import "./Appartement.css"
import Data from "../../pages/Accueil/data.json"
//import Caroussel from "../Caroussel/Caroussel";

function Appartement () {
    const appartements = Data
    const listAppartements = appartements.map((appartement) =>
    <li key={appartement.id}>
        <Link to={appartement.id}>
            <div className="article">
                <img className="article__cover" src={appartement.cover} alt={appartement.title}/>
                <p className="article__title">{appartement.title}</p>
            </div>
        </Link>
    </li>
    )

    return (
        <div id="articles" >
            {listAppartements}
        </div>
    )
}

export default Appartement
