import "./Dropdown.css"

function Dropdown(props) {
    return (
        <details className="dropdown">
            <summary className="dropdown__title">{props.name}</summary>
            <div className="dropdown__text">
                <p className="description">
                    {props.description}
                </p>
            </div>
        </details>
    )
}

export default Dropdown
