import { Routes, Route, useRouteMatch } from "react-router-dom";
import Banner from "./components/Banner/Banner"
import Footer from "./components/Footer/Footer"
import Accueil from "./pages/Accueil/Accueil";
import APropos from "./pages/aPropos/aPropos";
import Logement from "./pages/Logement/Logement";
import Erreur404 from "./pages/Erreur404/Erreur404"
import Data from "../src/pages/Accueil/data.json"
import "../src/App.css"

function App() {

  let match = useRouteMatch();

  return (
    <div className="App">
      <header className="App-header">
        <Banner />
      </header>
      <Routes>


        <Route index element={<Accueil />} />
        <Route path="/a-propos" element={<APropos />} />
        <Route path={`${match.path}/:id`} element={<Logement data={Data}/>} />  {/* /logement?xxxxx */}
        <Route path="*" element={ <Erreur404 />} />

      </Routes>
      <footer>
        <Footer />
      </footer>
    </div>
  );
}

export default App;
