import React, { Component } from "react";
import "./aPropos.css"

export default class APropos extends Component{
    constructor(props){
        super(props);
        this.state={
            count : 0
        }
    }

    render() {
        console.log(this.state.count)
        return (
            <div>
                <details>
                    <summary>Fiabilité</summary>
                    <div>Les annonces postées sur Kasa garantissent une fiabilité totale. Les photos sont conformes aux logements, et toutes les informations sont régulièrement vérifiées par nos équipes.</div>
                </details>

                <details>
                    <summary>Respect</summary>
                    <div>La bienveillance fait partie des valeurs fondatrices de Kasa. Tout comportement discriminatoire ou de perturbation du voisinage entraînera une exclusion de notre plateforme.</div>
                </details>

                <details>
                    <summary>Service</summary>
                    <div>Nos équipes se tiennent à votre disposition pour vous fournir une expérience parfaite. N'hésitez pas à nous contacter si vous avez la moindre question.</div>
                </details>
            </div>
        //<h1 onClick={this.inc.bind(this)}>A propos de {this.name} {this.state.count}</h1>
        )
    }
    
    /*inc(){
        this.setState({
            count : this.state.count+1
        })
    }*/
}
