import React, { Component } from "react";
import { Redirect } from "react-router-dom";
// import Dropdown from "../../components/Dropdown/Dropdown";
// import Caroussel from "../../components/Caroussel/Caroussel";
import LogementDetails from "../../components/LogementDetails/LogementDetails";
import Data from "../Accueil/data.json"

export default class Logement extends Component {
    constructor(props) {
        super(props);
        this.appartement = null;//getLogementById(props.match.params.id)
        console.log(this.appartement);
    }

    render() {
        return (
            <div id="logement">
                {/* <Caroussel /> */}
                {
                this.logement === null
                   ? <Redirect to="/404" />
                   : <LogementDetails data={this.appartement} />}
                {/* <Dropdown /> */}
            </div>
        )
    }
}

function getLogementById(id){
    for(const logement of Data){
        if (logement.id === id) {
            return logement
        }
    }
    console.log("echec")
    return null;
}
